import React from "react";
import PropTypes from "prop-types";
import { withStyles } from "@material-ui/core/styles";
import {
    Card,
    CardContent,
    CardMedia,
    IconButton,
    Typography
} from "@material-ui/core";

import SkipPreviousIcon from "@material-ui/icons/SkipPrevious";
import PlayArrowIcon from "@material-ui/icons/PlayArrow";
import SkipNextIcon from "@material-ui/icons/SkipNext";

const styles = theme => ({
    card: {
        display: "flex"
    },
    details: {
        display: "flex",
        flexDirection: "column"
    },
    content: {
        flex: "1 0 auto"
    },
    cover: {
        width: 151
    },
    controls: {
        display: "flex",
        alignItems: "center",
        paddingLeft: theme.spacing.unit,
        paddingBottom: theme.spacing.unit
    },
    playIcon: {
        height: 38,
        width: 38
    }
});

function MusicCard(props) {
    const { classes, theme } = props;

    return (
        <Card className={classes.card}>
            <div className={classes.details}>
                <CardContent className={classes.content} style={{height: 400}}>
                    {props.content}
                </CardContent>
                {props.showPlayer && <div className={classes.controls}>
                    <IconButton aria-label="Play/pause">
                        <PlayArrowIcon
                            className={classes.playIcon}
                            onClick={() => props.play()}
                        />
                    </IconButton>
                    <IconButton aria-label="Pause" onClick={() => props.pause("next")}>

                    </IconButton>
                </div>}

            </div>
            <CardMedia
                className={classes.cover}
                image="/static/images/cards/live-from-space.jpg"
                title="Live from space album cover"
            />
        </Card>
    );
}

MusicCard.propTypes = {
    classes: PropTypes.object.isRequired,
    theme: PropTypes.object.isRequired
};

export default withStyles(styles, { withTheme: true })(MusicCard);
