import React from 'react';
import BasicTable from "./table.jsx";
import animateActions from "./animate.js";
let cursor;
import MenuBar from "./components/menuBar";
import MusicCard from "./components/musicCard";
import { db } from "./components/db.js";


class Player extends React.Component {
    constructor(props) {
        super(props);
        this.state ={
            tableHeaders: ['Session Date', 'Duration', 'Play'],
            tableData: null,
            showPlayer: false,
            shouldPlayerPause: false
        }
    }
    componentDidMount() {
        this.getRecords();
    }

    async getRecords() {
        try {
            const callRes = await fetch('http://localhost:3000/test_db/getRecords', {
                // const res = await fetch('http://localhost:5000/test_db/saveRecord', {
                method: 'get',
                dataType: 'json',
                contentType: 'application/json; charset=utf-8'
            })
            const finalRes = await callRes.json();
            const failedToGetData = !(finalRes && finalRes.data && finalRes.statusCode === 200);
            if (failedToGetData) {
                console.error('Failed to get records');
            }
            const recordsData = finalRes.data.recordsData || [];
            const tableData = recordsData.map((record) =>{
                return {
                    sessionDate: record.sessionDate,
                    duration: record.duration,
                    play: <button onClick={this.handlePlayBtnClick.bind(this, record.recordEvents)}>Play</button>
                }
            });
            this.setState({tableData: tableData || [], recordsData: recordsData || []})
        } catch (ex) {
            console.error(ex)
        }
    }

    handlePlayBtnClick(steps) {
        this.setState({showPlayer: true}, this.play.bind(this, steps))
    }

    play(steps) {

        this.animate(steps)

    }

    animate(steps) {
        cursor = animateActions.showCursor(cursor);
        steps = steps || this.state.steps;
        let nowTime = this.state.nowTime || 0;
        let preTime = this.state.preTime || 0;
        let stepNumber = this.state.stepNumber || 0;
        let stepsCount = this.state.steps && this.state.steps.length || steps.length;
        let timeout = this.state.timeout || null;
        let currentStep = this.state.currentStep || null;

        currentStep = steps[stepNumber];
        if (currentStep.type === 'move') {
            cursor = animateActions.setCursorPosition(currentStep.x, currentStep.y, cursor);
        }

        if (currentStep.type === 'click') {
            cursor = animateActions.clickOnElementAtPosition(currentStep.x, currentStep.y, cursor);
        }

        if (currentStep.type === 'scroll') {
            window.scrollTo(currentStep.x, currentStep.y);
        }

        preTime = nowTime;
        nowTime = currentStep.time;

        stepNumber++;

        if (stepNumber === stepsCount) {
            console.log('Finished playing recording');
            window.clearTimeout(timeout);
            cursor = animateActions.hideCursor(cursor);
        } else {

            timeout = setTimeout(() => {
                if (!this.state.shouldPlayerPause) {
                    this.setState({
                        stepNumber: stepNumber,
                        steps: steps,
                        cursor: cursor,
                        timeout: timeout,
                        stepsCount: stepsCount,
                        currentStep: currentStep,
                        nowTime: nowTime,
                        preTime: preTime
                    });
                    this.animate()
                }
            }, preTime - nowTime);
        }
    }


    clearPlayerData (){
        this.setState({
            stepNumber: 0,
            steps: [],
            cursor: null,
            timeout: null,
            stepsCount: 0
        });
    }
    playPlayer = action => {
        this.play(this.state.steps)
    };
    pause = action => {
        this.setState({shouldPlayerPause: true});
    };


    render() {
        const content = this.state.showPlayer ? <div>hello</div> : <BasicTable
            tableHeaders={this.state.tableHeaders || []}
            tableData={this.state.tableData || []}
        />
        return (
            <div >
                <div id={"playerCanvas"} style={{margin: '50px auto', width: 700, }}>
                    <MenuBar showPlayer={this.state.showPlayer || false}/>
                    <MusicCard
                        data={db.alternative[this.state.current - 1]}
                        pause={() => this.pause()}
                        play={() => this.playPlayer()}
                        content={content}
                        showPlayer={this.state.showPlayer || false}/>
                </div>

            </div>
        );
    }
}
export default Player;
