import React from "react";
import { withStyles } from "@material-ui/core/styles";
import {Card, CardContent, IconButton,} from "@material-ui/core";
import PauseCircleFilledIcon from '@material-ui/icons/PauseCircleFilled';
import PlayCircleFilledIcon from '@material-ui/icons/PlayCircleFilled';import SkipNextIcon from "@material-ui/icons/SkipNext";

const styles = theme => ({
    card: {
        display: "flex"
    },
    details: {
        display: "flex",
        flexDirection: "column"
    },
    content: {
        flex: "1 0 auto"
    },
    cover: {
        width: 151
    },
    controls: {
        display: "flex",
        alignItems: "center",
        paddingLeft: theme.spacing.unit,
        paddingBottom: theme.spacing.unit
    },
    playIcon: {
        height: 38,
        width: 38
    }
});

function MusicCard(props) {
    const { classes, theme } = props;

    return (
        <Card className={classes.card}>
            <div className={classes.details}>
                <CardContent style={{width: 700, height: 400}}>
                    {props.content}
                </CardContent>
                {props.showPlayer && <div className={classes.controls}>
                    <IconButton aria-label="Play/pause">
                        <PlayCircleFilledIcon
                            className={classes.playIcon}
                            onClick={() => props.play()}
                        />
                    </IconButton>
                    <IconButton aria-label="Pause" onClick={() => props.pause("next")}>
                       <PauseCircleFilledIcon/>
                    </IconButton>
                </div>}

            </div>
        </Card>
    );
}


export default withStyles(styles, { withTheme: true })(MusicCard);
