module.exports.clickOnElementAtPosition = (x, y, screenProportions, cursor) => {
    let clickCandidate = document.elementFromPoint(x, y);

    while (clickCandidate && !clickCandidate.click) {
        clickCandidate = clickCandidate.parentNode;
    }

    if (clickCandidate && clickCandidate.click) {
        clickCandidate.click();
    }
    cursor.style.background = '#1b65d5';
    cursor.style.width = '50px';
    cursor.style.height = '50px';
    return cursor;
}

const getCursor = () => {
    let cursorNode = document.createElement('div');
    cursorNode = getDefaultCursorStyle(cursorNode);
    return cursorNode;
}

const getDefaultCursorStyle = (cursor) => {
    cursor.style.borderRadius = '50%';
    cursor.style.background = '#1c1c1c';
    cursor.style.width = '8px';
    cursor.style.height = '8px';
    cursor.style.position = 'fixed';
    cursor.style.top = 0;
    cursor.style.left = 0;
    cursor.style.zIndex = 999;

    return cursor;
}

module.exports.getCursor = getCursor;

module.exports.setCursorPosition = (x, y, screenProportions, playerScreenWidth, playerScreenHeight, cursor) => {
    cursor = getDefaultCursorStyle(cursor);
    const playerHeight = (playerScreenHeight) / screenProportions.height * 100
    const playerWidth = playerScreenWidth / screenProportions.width * 100
    cursor.style.left = ((x / playerWidth * 100) + ((document.documentElement.clientWidth / 2) - (playerScreenWidth / 2))) + 'px';
    cursor.style.top = ((y / playerHeight * 100) + 50 + 64) + 'px';
    return cursor;
}

module.exports.showCursor = (cursor) => {
    cursor = cursor || this.getCursor();
    document.body.appendChild(cursor)
    return cursor;
}

module.exports.hideCursor = (cursor) => {
    document.body.removeChild(cursor)
     return cursor;
}
