import React from 'react';
import BasicTable from "./table.jsx";
import animateActions from "./animate.js";
let cursor;
import MenuBar from "./components/menuBar";
import MusicCard from "./components/musicCard";
import { db } from "./components/db.js";


class Player extends React.Component {
    constructor(props) {
        super(props);
        this.state ={
            tableHeaders: ['Session Date', 'Duration', 'Play'],
            tableData: null,
            showPlayer: false,
            shouldPlayerPause: false,
            isPlayingNow: false,
            recordFinishedPlaying: true
        }
    }
    componentDidMount() {
        this.getRecords();
    }

    async getRecords() {
        try {
            const callRes = await fetch('http://localhost:3000/test_db/getRecords', {
                // const res = await fetch('http://localhost:5000/test_db/saveRecord', {
                method: 'get',
                dataType: 'json',
                contentType: 'application/json; charset=utf-8'
            })
            const finalRes = await callRes.json();
            const failedToGetData = !(finalRes && finalRes.data && finalRes.statusCode === 200);
            if (failedToGetData) {
                console.error('Failed to get records');
            }
            const recordsData = finalRes.data.recordsData || [];
            const tableData = recordsData.map((record) =>{
                return {
                    sessionDate: record.sessionDate,
                    duration: record.duration,
                    play: <button onClick={this.handlePlayBtnClick.bind(this, record.recordEvents)}>Play</button>
                }
            });
            this.setState({tableData: tableData || [], recordsData: recordsData || []})
        } catch (ex) {
            console.error(ex)
        }
    }

    handlePlayBtnClick(steps) {
        if(this.state.cursor){
            cursor = animateActions.hideCursor(this.state.cursor);
        }
        if(this.state.timeout){
            window.clearTimeout(this.state.timeout);
        }
        this.setState({showPlayer: true, shouldPlayerPause: false}, this.play.bind(this, steps))
    }

    play(steps) {
        this.setState({isPlayingNow: true, recordFinishedPlaying: false});
        this.animate(steps)
    }

    animate(steps) {
        let nowTime = this.state.nowTime;
        let preTime = this.state.preTime;
        let stepNumber = this.state.stepNumber;
        let stepsCount = this.state.steps && this.state.steps.length;
        let timeout = this.state.timeout;
        let currentStep = this.state.currentStep;
        let cursor = this.state.cursor;

        if (steps) {
            nowTime = 0;
            preTime = 0;
            stepNumber = 0;
            stepsCount = steps.length;
            timeout = null;
            currentStep = null;
            cursor = null;
        }
        steps = steps || this.state.steps;
        cursor = animateActions.showCursor(cursor);

        currentStep = steps[stepNumber];
        if (currentStep.type === 'move') {
            cursor = animateActions.setCursorPosition(currentStep.x, currentStep.y, currentStep.screenProportions, cursor);
        }

        if (currentStep.type === 'click') {
            cursor = animateActions.clickOnElementAtPosition(currentStep.x, currentStep.y, currentStep.screenProportions, cursor);
        }

        if (currentStep.type === 'scroll') {
            window.scrollTo(currentStep.x, currentStep.y);
        }

        preTime = nowTime;
        nowTime = currentStep.time;

        stepNumber++;

        if (stepNumber === stepsCount) {
            console.log('Finished playing recording');
            window.clearTimeout(timeout);
            cursor = animateActions.hideCursor(cursor);
            this.clearPlayerData();
        } else {

            timeout = setTimeout(() => {
                if (!this.state.shouldPlayerPause) {
                    this.setState({
                        stepNumber: stepNumber,
                        steps: steps,
                        cursor: cursor,
                        timeout: timeout,
                        stepsCount: stepsCount,
                        currentStep: currentStep,
                        nowTime: nowTime,
                        preTime: preTime
                    });
                    this.animate()
                }
            }, preTime - nowTime + 50);
        }
    }


    clearPlayerData (){
        this.setState({
            stepNumber: 0,
            steps: [],
            cursor: null,
            timeout: null,
            stepsCount: 0,
            isPlayingNow: false,
            recordFinishedPlaying: true
        });
    }
    playPlayer = action => {
        if(!this.state.recordFinishedPlaying){
            this.setState({shouldPlayerPause: false});
            this.play();
        } else{
            this.setState({showPlayer: false});
        }
    };
    pause = action => {
        this.setState({shouldPlayerPause: true, isPlayingNow: false, recordFinishedPlaying: false});
    };
    handleListClick = action => {
        this.setState({shouldPlayerPause: true, showPlayer: false, isPlayingNow: false});
    };


    render() {
        const content = this.state.showPlayer ? '' : <BasicTable
            tableHeaders={this.state.tableHeaders || []}
            tableData={this.state.tableData || []}
        />
        return (
            <div >
                <div id={"playerCanvas"} style={{
                    margin: '50px auto',
                    width: 700,
                }}>
                    <MenuBar showPlayer={this.state.showPlayer || false}
                    handleListClick={() => this.handleListClick()}/>
                    <MusicCard
                        data={db.alternative[this.state.current - 1]}
                        pause={() => this.pause()}
                        play={() => this.playPlayer()}
                        content={content}
                        isPlayingNow={this.state.isPlayingNow || false}
                        showPlayer={this.state.showPlayer || false}/>
                </div>

            </div>
        );
    }
}
export default Player;
